    y_p2 = randint(-10,48)
    x_p2 = 240 +140

    #part_game loop
    while True:
        pgb.sound(0)
        # update the screen :
        if pgb.button_Home():
            pgb.fill(PicoGameBoy.color(0,0,0))
            pgb.show()
            machine.reset()
